import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card, ListGroup, Alert, Modal } from 'react-bootstrap';
import axios from 'axios';
import MCQForm from '../components/MCQForm';
import Chat from '../components/Chat';
import Spinner from '../components/Spinner';

function TeacherDashboard() {
  const [exam, setExam] = useState({ title: '', timer: 0, deadline: '', questions: [] });
  const [exams, setExams] = useState([]);
  const [selectedExamId, setSelectedExamId] = useState('');
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [createdExamCode, setCreatedExamCode] = useState('');
  const [showChat, setShowChat] = useState(false);
  const [chatStudentId, setChatStudentId] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    // Fetch teacher's exams
    fetchExams();
  }, []);

  const fetchExams = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/exams', { headers: { Authorization: `Bearer ${token}` } });
      setExams(res.data);
    } catch (err) {
      console.error('Error fetching exams:', err);
    }
  };

  const addQuestion = (question) => {
    setExam({ ...exam, questions: [...exam.questions, question] });
  };

  const generateCode = () => {
    return Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);
  };

  const createExam = async () => {
    if (!exam.title || !exam.timer || !exam.deadline || exam.questions.length === 0) {
      setError('Please fill all fields and add at least one question.');
      return;
    }
    const code = generateCode();
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/exams', { ...exam, code }, { headers: { Authorization: `Bearer ${token}` } });
      setExams([...exams, res.data.exam]);
      setCreatedExamCode(code);
      alert(`Exam created successfully! Share this code with students: ${code}`);
      setExam({ title: '', timer: 0, deadline: '', questions: [] });
      setError('');
    } catch (err) {
      console.error('Error creating exam:', err.response?.data || err.message);
      setError(err.response?.data?.message || 'Error creating exam. Check console for details.');
    } finally {
      setLoading(false);
    }
  };

  const viewScores = async () => {
    if (!selectedExamId) {
      setError('Please select an exam.');
      return;
    }
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:5000/api/exams/${selectedExamId}/submissions`, { headers: { Authorization: `Bearer ${token}` } });
      setSubmissions(res.data);
    } catch (err) {
      setError('Error fetching scores.');
    } finally {
      setLoading(false);
    }
  };

  const startChat = (studentId) => {
    setChatStudentId(studentId);
    setShowChat(true);
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col md={8}>
          <h2><i className="bi bi-pencil-square"></i> Create Exam</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          {loading && <Spinner />}
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Name the Exam</Form.Label>
              <Form.Control type="text" value={exam.title} onChange={(e) => setExam({ ...exam, title: e.target.value })} required />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Add Time in Minutes</Form.Label>
              <Form.Control type="number" value={exam.timer} onChange={(e) => setExam({ ...exam, timer: Number(e.target.value) })} required />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Add Deadline</Form.Label>
              <Form.Control type="datetime-local" value={exam.deadline} onChange={(e) => setExam({ ...exam, deadline: e.target.value })} required />
            </Form.Group>
          </Form>
          <h4>Add MCQ Questions</h4>
          <MCQForm onAddQuestion={addQuestion} />
          <div className="mt-3">
            <h5>Questions Added: {exam.questions.length}</h5>
            {exam.questions.map((q, idx) => (
              <Card key={idx} className="mb-2">
                <Card.Body>
                  <p><strong>Q{idx + 1}:</strong> {q.question}</p>
                  <ul>
                    {q.options.map((opt, optIdx) => (
                      <li key={optIdx}>{opt} {optIdx === q.correctOption ? '(Correct)' : ''}</li>
                    ))}
                  </ul>
                </Card.Body>
              </Card>
            ))}
          </div>
          <Button onClick={createExam} variant="success" disabled={loading} className="mt-3">
            <i className="bi bi-check-circle"></i> Create Exam
          </Button>
          {createdExamCode && (
            <Alert variant="info" className="mt-3">
              <strong>Exam Code:</strong> {createdExamCode} (Share this with students)
            </Alert>
          )}
        </Col>
        <Col md={4}>
          <h3><i className="bi bi-bar-chart"></i> View Scores</h3>
          <Form.Group className="mb-3">
            <Form.Label>Select Exam</Form.Label>
            <Form.Select value={selectedExamId} onChange={(e) => setSelectedExamId(e.target.value)}>
              <option value="">Choose...</option>
              {exams.map((e) => <option key={e._id} value={e._id}>{e.title}</option>)}
            </Form.Select>
          </Form.Group>
          <Button onClick={viewScores} variant="primary" className="mb-3" disabled={loading}>
            <i className="bi bi-eye"></i> View Scores
          </Button>
          {loading && <Spinner />}
          <ListGroup>
            {submissions.map((sub, idx) => (
              <ListGroup.Item key={idx} className="d-flex justify-content-between align-items-center">
                <div>
                  {sub.studentId?.name || 'Unknown'} - Score: {sub.score}/{exams.find(e => e._id === selectedExamId)?.questions?.length || 0} ({sub.remark})
                </div>
                <Button variant="link" onClick={() => startChat(sub.studentId?._id)} size="sm">
                  <i className="bi bi-chat"></i> Start Chat
                </Button>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>

      <Modal show={showChat} onHide={() => setShowChat(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Chat with Student</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Chat receiverId={chatStudentId} examId={selectedExamId} />
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default TeacherDashboard;
