import React, { useState } from 'react';
import { Container, Form, Button, Card } from 'react-bootstrap';
import Timer from '../components/Timer';

function ExamPage({ exam, onSubmit }) {
  const [answers, setAnswers] = useState(Array(exam.questions.length).fill(null));

  const handleSubmit = () => {
    onSubmit(answers);
  };

  const handleTimeUp = () => {
    handleSubmit();
  };

  return (
    <Container className="mt-5">
      <Timer duration={exam.timer} onTimeUp={handleTimeUp} />
      <h2>{exam.title}</h2>
      {exam.questions.map((q, idx) => (
        <Card key={idx} className="mb-3">
          <Card.Body>
            <p>{q.question}</p>
            {q.options.map((opt, optIdx) => (
              <Form.Check
                key={optIdx}
                type="radio"
                label={opt}
                name={`question-${idx}`}
                onChange={() => {
                  const newAnswers = [...answers];
                  newAnswers[idx] = optIdx;
                  setAnswers(newAnswers);
                }}
              />
            ))}
          </Card.Body>
        </Card>
      ))}
      <Button onClick={handleSubmit} variant="success">Submit Exam</Button>
    </Container>
  );
}

export default ExamPage;
