import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import Chat from '../components/Chat';
import Spinner from '../components/Spinner';
import ExamPage from './ExamPage';

const gradientStyle = {
  background: 'linear-gradient(90deg, #ee7752, #e73c7e, #23d5ab, #23a6d5)',
  WebkitBackgroundClip: 'text',
  WebkitTextFillColor: 'transparent',
  fontWeight: 'bold',
};

const alertGradient = {
  background: 'linear-gradient(90deg, #ff416c, #833ab4)',
  color: 'white',
  fontWeight: 'bold',
  padding: '10px',
  borderRadius: '8px',
};

function StudentDashboard() {
  const [form, setForm] = useState({ name: '', rollNo: '', code: '' });
  const [exam, setExam] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);
  const [remark, setRemark] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  const startExam = async () => {
    setLoading(true);
    setError('');
    setExam(null);
    try {
      const res = await axios.get(`http://localhost:5000/api/exams/${form.code}`);
      setExam(res.data);
    } catch (err) {
      if (err.response && err.response.status === 403 && err.response.data.message === 'Deadline passed') {
        setError('Oops, no questions available. Seems like you have missed the deadline.');
      } else if (err.response && err.response.status === 404) {
        setError('Exam not found. Please check your exam code.');
      } else {
        setError('Failed to load the exam. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const submitExam = async (answers) => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.post(
        'http://localhost:5000/api/exams/submit',
        { examId: exam._id, answers, rollNo: form.rollNo },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setScore(res.data.score);
      setRemark(res.data.remark);
      setSubmitted(true);
    } catch (err) {
      setError('Error submitting exam. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (submitted) {
    return (
      <Container className="mt-5">
        <Card className="text-center p-4">
          <h2 style={gradientStyle}>Your Score: {score}/{exam.questions.length}</h2>
          <p style={{ fontWeight: 'bold' }}>Remark: {remark}</p>
          <Chat receiverId={exam.teacherId} examId={exam._id} />
          <Button
            variant="primary"
            className="mt-3"
            onClick={() => {
              setSubmitted(false);
              setExam(null);
              setForm({ name: '', rollNo: '', code: '' });
              setScore(0);
              setRemark('');
            }}
          >
            Take Another Exam
          </Button>
        </Card>
      </Container>
    );
  }

  return (
    <Container className="mt-5" style={{ color: 'white' }}>
      <Row className="justify-content-center">
        <Col md={6}>
          <h2 style={gradientStyle} className="mb-4">
            Enter Exam Details
          </h2>

          {error && (
            <Alert style={alertGradient} variant="danger">
              {error}
            </Alert>
          )}

          {loading && <Spinner />}

          {!exam && (
            <>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Name</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter your name"
                    value={form.name}
                    onChange={(e) => setForm({ ...form, name: e.target.value })}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Roll No</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter your roll number"
                    value={form.rollNo}
                    onChange={(e) => setForm({ ...form, rollNo: e.target.value })}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Exam Code</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter exam code"
                    value={form.code}
                    onChange={(e) => setForm({ ...form, code: e.target.value })}
                    required
                  />
                </Form.Group>

                <Button
                  style={{ background: 'linear-gradient(90deg, #007bff, #6a0dad)', border: 'none' }}
                  variant="primary"
                  onClick={(e) => {
                    e.preventDefault();
                    startExam();
                  }}
                  disabled={loading}
                  className="w-100"
                >
                  Start Exam
                </Button>
              </Form>
            </>
          )}

          {exam && <ExamPage exam={exam} onSubmit={submitExam} />}
        </Col>
      </Row>
    </Container>
  );
}

export default StudentDashboard;
