import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { Form, Button, ListGroup } from 'react-bootstrap';

const socket = io('http://localhost:5000'); // Ensure this matches your backend URL

function Chat({ receiverId, examId }) {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Connect to socket
    socket.on('connect', () => {
      console.log('Connected to server');
      setIsConnected(true);
      // Join a private room for the chat (using examId or a unique room ID)
      socket.emit('joinRoom', { examId, userId: localStorage.getItem('userId') });
    });

    socket.on('disconnect', () => {
      console.log('Disconnected from server');
      setIsConnected(false);
    });

    // Listen for incoming messages
    socket.on('receiveMessage', (data) => {
      console.log('Received message:', data);
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    // Listen for chat history (if implemented in backend)
    socket.on('chatHistory', (history) => {
      setMessages(history);
    });

    // Cleanup on unmount
    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('receiveMessage');
      socket.off('chatHistory');
    };
  }, [examId]);

  useEffect(() => {
    // Scroll to bottom when new message arrives
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && receiverId) {
      const messageData = {
        senderId: localStorage.getItem('userId'),
        receiverId,
        message,
        examId,
        timestamp: new Date().toISOString(),
      };
      console.log('Sending message:', messageData);
      socket.emit('sendMessage', messageData);
      setMessages((prevMessages) => [...prevMessages, messageData]); // Add to local UI immediately
      setMessage('');
    }
  };

  return (
    <div className="chat-container">
      <h4>Chat with {receiverId ? 'Student' : 'Teacher'}</h4>
      <div className="connection-status">
        Status: {isConnected ? 'Connected' : 'Disconnected'}
      </div>
      <div className="messages-list" style={{ height: '300px', overflowY: 'auto', border: '1px solid #ddd', padding: '10px' }}>
        {messages.length === 0 ? (
          <p>No messages yet.</p>
        ) : (
          messages.map((msg, index) => (
            <div key={index} className={`message ${msg.senderId === localStorage.getItem('userId') ? 'sent' : 'received'}`}>
              <strong>{msg.senderId === localStorage.getItem('userId') ? 'You' : 'Them'}:</strong> {msg.message}
              <small> ({new Date(msg.timestamp).toLocaleTimeString()})</small>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>
      <Form onSubmit={sendMessage}>
        <Form.Group className="mb-3">
          <Form.Control
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type a message..."
            disabled={!isConnected}
          />
        </Form.Group>
        <Button type="submit" disabled={!isConnected || !message.trim()}>
          Send
        </Button>
      </Form>
    </div>
  );
}

export default Chat;
