   import React, { useState, useEffect } from 'react';
   import { Container, Row, Col, Form, Button, Card, ListGroup, Alert } from 'react-bootstrap';
   import axios from 'axios';
   import MCQForm from '../components/MCQForm';
   import Chat from '../components/Chat';
   import Spinner from '../components/Spinner';

   function TeacherDashboard() {
     const [exam, setExam] = useState({ title: '', questions: [], timer: 0, deadline: '', code: '' });
     const [exams, setExams] = useState([]);  // List of created exams
     const [selectedExamId, setSelectedExamId] = useState('');
     const [submissions, setSubmissions] = useState([]);
     const [loading, setLoading] = useState(false);
     const [error, setError] = useState('');
     const token = localStorage.getItem('token');

     useEffect(() => {
       // Fetch teacher's exams (assuming backend has an endpoint; add if needed)
       // For now, assume exams are listed after creation
     }, []);

     const addQuestion = (question) => {
       setExam({ ...exam, questions: [...exam.questions, question] });
     };

     const createExam = async () => {
       setLoading(true);
       try {
         const res = await axios.post('http://localhost:5000/api/exams', exam, { headers: { Authorization: `Bearer ${token}` } });
         setExams([...exams, res.data]);
         alert('Exam created successfully!');
         setExam({ title: '', questions: [], timer: 0, deadline: '', code: '' });
       } catch (err) {
         setError(err.response?.data?.message || 'Error creating exam');
       } finally {
         setLoading(false);
       }
     };

     const viewScores = async () => {
       if (!selectedExamId) return setError('Please select an exam');
       setLoading(true);
       try {
         const res = await axios.get(`http://localhost:5000/api/exams/${selectedExamId}/submissions`, { headers: { Authorization: `Bearer ${token}` } });
         setSubmissions(res.data);
       } catch (err) {
         setError('Error fetching scores');
       } finally {
         setLoading(false);
       }
     };

     return (
       <Container className="mt-5">
         <Row>
           <Col md={8}>
             <h2><i className="bi bi-pencil-square"></i> Create Exam</h2>
             {error && <Alert variant="danger">{error}</Alert>}
             {loading && <Spinner />}
             <Form>
               <Form.Group className="mb-3">
                 <Form.Label>Title of Exam</Form.Label>
                 <Form.Control type="text" value={exam.title} onChange={(e) => setExam({ ...exam, title: e.target.value })} required />
               </Form.Group>
               <Form.Group className="mb-3">
                 <Form.Label>Select Time in Mins</Form.Label>
                 <Form.Control type="number" value={exam.timer} onChange={(e) => setExam({ ...exam, timer: Number(e.target.value) })} required />
               </Form.Group>
               <Form.Group className="mb-3">
                 <Form.Label>Add Deadline</Form.Label>
                 <Form.Control type="datetime-local" value={exam.deadline} onChange={(e) => setExam({ ...exam, deadline: e.target.value })} required />
               </Form.Group>
               <Form.Group className="mb-3">
                 <Form.Label>Exam Code</Form.Label>
                 <Form.Control type="text" value={exam.code} onChange={(e) => setExam({ ...exam, code: e.target.value })} required />
               </Form.Group>
             </Form>
             <MCQForm onAddQuestion={addQuestion} />
             <Button onClick={createExam} variant="success" disabled={loading}>
               <i className="bi bi-check-circle"></i> Create Exam
             </Button>
           </Col>
           <Col md={4}>
             <h3><i className="bi bi-bar-chart"></i> View Scores</h3>
             <Form.Group className="mb-3">
               <Form.Label>Select Exam</Form.Label>
               <Form.Select value={selectedExamId} onChange={(e) => setSelectedExamId(e.target.value)}>
                 <option value="">Choose...</option>
                 {exams.map((e) => <option key={e._id} value={e._id}>{e.title}</option>)}
               </Form.Select>
             </Form.Group>
             <Button onClick={viewScores} variant="primary" className="mb-3" disabled={loading}>
               <i className="bi bi-eye"></i> View Scores
             </Button>
             {loading && <Spinner />}
             <ListGroup>
               {submissions.map((sub, idx) => (
                 <ListGroup.Item key={idx} className="d-flex justify-content-between align-items-center">
                   <div>
                     {sub.studentId.name} (Roll: {sub.studentId.rollNo}) - Score: {sub.score}/{exams.find(e => e._id === selectedExamId)?.questions.length || 0}
                   </div>
                   <Button variant="link" onClick={() => alert('Chat opened')} size="sm">
                     <i className="bi bi-chat"></i> Start Chat
                   </Button>
                 </ListGroup.Item>
               ))}
             </ListGroup>
           </Col>
         </Row>
       </Container>
     );
   }

   export default TeacherDashboard;
