import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card, ListGroup, Alert } from 'react-bootstrap';
import axios from 'axios';
import MCQForm from '../components/MCQForm';
import Chat from '../components/Chat';

function TeacherDashboard() {
  const [exam, setExam] = useState({ title: '', timer: 0, deadline: '', questions: [] });
  const [createdCode, setCreatedCode] = useState('');
  const [error, setError] = useState('');
  const [exams, setExams] = useState([]);
  const [selectedExamId, setSelectedExamId] = useState('');
  const [submissions, setSubmissions] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchExams();
  }, []);

  const fetchExams = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/exams', { headers: { Authorization: `Bearer ${token}` } });
      setExams(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const addQuestion = (q) => {
    setExam({ ...exam, questions: [...exam.questions, q] });
  };

  const createExam = async () => {
    if (!exam.title || !exam.timer || !exam.deadline || exam.questions.length === 0) {
      setError('All fields including at least one question are required.');
      return;
    }
    setError('');
    try {
      const res = await axios.post(
        'http://localhost:5000/api/exams',
        exam,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCreatedCode(res.data.exam.code);
      setExam({ title: '', timer: 0, deadline: '', questions: [] });
      fetchExams();
    } catch (err) {
      if (err.response?.data?.message) setError(err.response.data.message);
      else setError('Failed to create exam.');
      console.error(err);
    }
  };

  const viewScores = async () => {
    if (!selectedExamId) {
      setError('Please select an exam.');
      return;
    }
    try {
      const res = await axios.get(`http://localhost:5000/api/exams/${selectedExamId}/submissions`, { headers: { Authorization: `Bearer ${token}` } });
      setSubmissions(res.data);
    } catch (err) {
      setError('Failed to fetch submissions.');
      console.error(err);
    }
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col md={8}>
          <h2>Create Exam</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Exam Title</Form.Label>
              <Form.Control value={exam.title} onChange={e => setExam({ ...exam, title: e.target.value })} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Time in Minutes</Form.Label>
              <Form.Control type="number" value={exam.timer} onChange={e => setExam({ ...exam, timer: Number(e.target.value) })} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Deadline</Form.Label>
              <Form.Control type="datetime-local" value={exam.deadline} onChange={e => setExam({ ...exam, deadline: e.target.value })} />
            </Form.Group>
            <MCQForm onAddQuestion={addQuestion} />
            <Button className="mt-3" onClick={createExam}>Create Exam</Button>

            {createdCode && (
              <Alert variant="info" className="mt-3">
                Exam created! Share this code with your students: <strong>{createdCode}</strong>
              </Alert>
            )}
          </Form>
        </Col>

        <Col md={4}>
          <h3>View Scores</h3>
          <Form.Group>
            <Form.Label>Select Exam</Form.Label>
            <Form.Select onChange={e => setSelectedExamId(e.target.value)} value={selectedExamId}>
              <option value="">-- Select --</option>
              {exams.map(ex => <option key={ex._id} value={ex._id}>{ex.title}</option>)}
            </Form.Select>
          </Form.Group>
          <Button className="mt-2" onClick={viewScores}>View Scores</Button>

          <ListGroup className="mt-3">
            {submissions.map(sub => (
              <ListGroup.Item key={sub._id} className="d-flex justify-content-between align-items-center">
                <div>
                  {sub.studentId?.name || 'Unknown'} (Roll: {sub.rollNo || 'N/A'}) - Score: {sub.score} ({sub.remark})
                </div>
                <Button variant="link" className="p-0" onClick={() => alert('Start chat with student - implement this')}>
                  <i className="bi bi-chat"></i> Start Chat
                </Button>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
}

export default TeacherDashboard;
