import React, { useState } from 'react';
import { Form, Button, Card } from 'react-bootstrap';

function MCQForm({ onAddQuestion, isDemo = false }) {
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState(['', '', '', '']);
  const [correctOption, setCorrectOption] = useState(0);

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddQuestion({ question, options, correctOption });
    setQuestion('');
    setOptions(['', '', '', '']);
    setCorrectOption(0);
  };

  return (
    <Card className="mb-3">
      <Card.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Question</Form.Label>
            <Form.Control
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              required
            />
          </Form.Group>
          {options.map((opt, idx) => (
            <Form.Group key={idx} className="mb-2">
              <Form.Label>Option {idx + 1}</Form.Label>
              <Form.Control
                type="text"
                value={opt}
                onChange={(e) => {
                  const newOpts = [...options];
                  newOpts[idx] = e.target.value;
                  setOptions(newOpts);
                }}
                required
              />
            </Form.Group>
          ))}
          <Form.Group className="mb-3">
            <Form.Label>Correct Option</Form.Label>
            <Form.Select value={correctOption} onChange={(e) => setCorrectOption(Number(e.target.value))}>
              {options.map((_, idx) => <option key={idx} value={idx}>Option {idx + 1}</option>)}
            </Form.Select>
          </Form.Group>
          <Button type="submit" variant="primary">{isDemo ? 'Add Demo Question' : 'Add Question'}</Button>
        </Form>
      </Card.Body>
    </Card>
  );
}

export default MCQForm;
