import React, { useState, useEffect } from 'react';
import { Form, Button, ListGroup, Card } from 'react-bootstrap';
import axios from 'axios';

function Chat({ receiverId, examId }) {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchMessages();
  }, [receiverId]);

  const fetchMessages = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/chats/${receiverId}`, { headers: { Authorization: `Bearer ${token}` } });
      setMessages(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const sendMessage = async () => {
    if (!message) return;
    try {
      await axios.post('http://localhost:5000/api/chats', { receiverId, message }, { headers: { Authorization: `Bearer ${token}` } });
      setMessage('');
      fetchMessages();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Card className="mt-4">
      <Card.Body>
        <h5>Chat</h5>
        <ListGroup style={{ maxHeight: '300px', overflowY: 'auto' }}>
          {messages.map(msg => (
            <ListGroup.Item key={msg._id}>
              <strong>{msg.senderId === localStorage.getItem('userId') ? 'You' : 'Them'}:</strong> {msg.message}
            </ListGroup.Item>
          ))}
        </ListGroup>
        <Form className="mt-3">
          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Type a message"
              value={message}
              onChange={e => setMessage(e.target.value)}
            />
          </Form.Group>
          <Button onClick={sendMessage} variant="primary" className="mt-2">Send</Button>
        </Form>
      </Card.Body>
    </Card>
  );
}

export default Chat;
