import React, { useState } from "react";
import axios from "axios";

function TeacherDashboard() {
  const [title, setTitle] = useState("");
  const [timer, setTimer] = useState("");
  const [deadline, setDeadline] = useState("");
  const [questions, setQuestions] = useState([{ question: "", options: ["", "", "", ""], correctOption: "" }]);
  const [examCode, setExamCode] = useState(null);
  const [message, setMessage] = useState("");

  // âœ… Handle adding new question
  const addQuestion = () => {
    setQuestions([...questions, { question: "", options: ["", "", "", ""], correctOption: "" }]);
  };

  // âœ… Handle input change for question and options
  const handleQuestionChange = (index, field, value) => {
    const updated = [...questions];
    updated[index][field] = value;
    setQuestions(updated);
  };

  const handleOptionChange = (qIndex, optIndex, value) => {
    const updated = [...questions];
    updated[qIndex].options[optIndex] = value;
    setQuestions(updated);
  };

  // âœ… Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setExamCode(null);

    try {
      const token = localStorage.getItem("token"); // teacher auth token
      const res = await axios.post(
        "http://localhost:5000/api/exams/create",
        { title, timer, deadline, questions },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage(res.data.message);
      setExamCode(res.data.exam.code); // âœ… Store the generated code
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || "Error creating exam.");
    }
  };

  // âœ… Copy exam code to clipboard
  const handleCopy = () => {
    navigator.clipboard.writeText(examCode);
    alert("Exam code copied!");
  };

  return (
    <div className="min-h-screen bg-gray-950 text-white p-8 flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-4 text-green-400">Create New Exam</h1>

      <form onSubmit={handleSubmit} className="bg-gray-900 p-6 rounded-2xl shadow-xl w-full max-w-3xl">
        <div className="mb-3">
          <label className="block text-sm text-gray-400 mb-1">Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full p-2 rounded bg-gray-800 text-white"
            required
          />
        </div>

        <div className="mb-3">
          <label className="block text-sm text-gray-400 mb-1">Timer (minutes)</label>
          <input
            type="number"
            value={timer}
            onChange={(e) => setTimer(e.target.value)}
            className="w-full p-2 rounded bg-gray-800 text-white"
            required
          />
        </div>

        <div className="mb-3">
          <label className="block text-sm text-gray-400 mb-1">Deadline</label>
          <input
            type="datetime-local"
            value={deadline}
            onChange={(e) => setDeadline(e.target.value)}
            className="w-full p-2 rounded bg-gray-800 text-white"
            required
          />
        </div>

        {questions.map((q, qIndex) => (
          <div key={qIndex} className="mb-6 border-b border-gray-700 pb-4">
            <label className="block text-sm text-gray-400 mb-1">
              Question {qIndex + 1}
            </label>
            <input
              type="text"
              value={q.question}
              onChange={(e) => handleQuestionChange(qIndex, "question", e.target.value)}
              className="w-full p-2 rounded bg-gray-800 text-white mb-2"
              placeholder="Enter question"
              required
            />

            {q.options.map((opt, optIndex) => (
              <input
                key={optIndex}
                type="text"
                value={opt}
                onChange={(e) => handleOptionChange(qIndex, optIndex, e.target.value)}
                className="w-full p-2 rounded bg-gray-800 text-white mb-2"
                placeholder={`Option ${optIndex + 1}`}
                required
              />
            ))}

            <select
              value={q.correctOption}
              onChange={(e) => handleQuestionChange(qIndex, "correctOption", e.target.value)}
              className="w-full p-2 rounded bg-gray-800 text-white"
              required
            >
              <option value="">Select correct option</option>
              {q.options.map((opt, i) => (
                <option key={i} value={opt}>
                  {opt || `Option ${i + 1}`}
                </option>
              ))}
            </select>
          </div>
        ))}

        <button
          type="button"
          onClick={addQuestion}
          className="bg-blue-600 px-4 py-2 rounded hover:bg-blue-700 mr-4"
        >
          + Add Question
        </button>

        <button
          type="submit"
          className="bg-green-600 px-4 py-2 rounded hover:bg-green-700"
        >
          Create Exam
        </button>
      </form>

      {message && <p className="mt-4 text-yellow-400">{message}</p>}

      {examCode && (
        <div className="mt-6 bg-gray-800 p-4 rounded-xl text-center shadow-lg">
          <p className="text-lg">Your Exam Code:</p>
          <h2 className="text-3xl font-bold text-green-400 mt-2">{examCode}</h2>
          <button
            onClick={handleCopy}
            className="mt-3 bg-purple-600 px-4 py-2 rounded hover:bg-purple-700"
          >
            ðŸ“‹ Copy Code
          </button>
        </div>
      )}
    </div>
  );
}

export default TeacherDashboard;
