import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import Chat from '../components/Chat';
import Spinner from '../components/Spinner';

function StudentDashboard() {
  const [form, setForm] = useState({ name: '', rollNo: '', code: '' });
  const [exam, setExam] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);
  const [remark, setRemark] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  const startExam = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:5000/api/exams/${form.code}`);
      setExam(res.data);
      setError('');
    } catch (err) {
      setError('Oops, no questions available. Seems like you have missed the deadline.');
    } finally {
      setLoading(false);
    }
  };

  const submitExam = async (answers) => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/exams/submit', { examId: exam._id, answers }, { headers: { Authorization: `Bearer ${token}` } });
      setScore(res.data.score);
      setRemark(res.data.remark);
      setSubmitted(true);
    } catch (err) {
      setError('Error submitting exam');
    } finally {
      setLoading(false);
    }
  };

  if (submitted) {
    return (
      <Container className="mt-5">
        <Card className="text-center">
          <Card.Body>
            <i className="bi bi-trophy display-4 text-success mb-3"></i>
            <h3>Your Score: {score}/{exam.questions.length}</h3>
            <p className="lead">Remark: {remark}</p>
            <Chat receiverId={exam.teacherId} examId={exam._id} />
          </Card.Body>
        </Card>
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <h2><i className="bi bi-key"></i> Enter Exam Details</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          {loading && <Spinner />}
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control type="text" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} required />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Roll No</Form.Label>
              <Form.Control type="text" value={form.rollNo} onChange={(e) => setForm({ ...form, rollNo: e.target.value })} required />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Exam Code</Form.Label>
              <Form.Control type="text" value={form.code} onChange={(e) => setForm({ ...form, code: e.target.value })} required />
            </Form.Group>
            <Button onClick={startExam} variant="primary" disabled={loading}>
              <i className="bi bi-play"></i> Start Exam
            </Button>
          </Form>
        </Col>
      </Row>
      {exam && <ExamPage exam={exam} onSubmit={submitExam} />}
    </Container>
  );
}

export default StudentDashboard;
